# 类装饰器装饰函数
class Outer():

    # 魔术方法：当把该类的对象当做函数调用时，自动触发obj()
    def __call__(self, func):
        self.func = func
        return self.inner

    def inner(self, who):
        print(f'和{who}一起看电影啦')
        self.func(who)
        print('约会吃烤肉啦哇')


# 注意这里应该是Outer()
# Outer() ===> obj,  @obj ===>obj(date) , call （date）===> inner()
@Outer()
def date(who):
    print(f'今天和{who}约会啦')


date('duanduan')
# 和duanduan一起看电影啦
# 今天和duanduan约会啦
# 约会吃烤肉啦哇

